import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

export const api = {
  // Товары
  getProducts: () => axios.get(`${API_URL}/products`),
  createProduct: (product) => axios.post(`${API_URL}/products`, product),
  updateProduct: (id, product) => axios.put(`${API_URL}/products/${id}`, product),
  deleteProduct: (id) => axios.delete(`${API_URL}/products/${id}`),

  // Заказы
  getOrders: () => axios.get(`${API_URL}/orders`),
  createOrder: (order) => axios.post(`${API_URL}/orders`, order),
  updateOrderStatus: (id, status) => axios.put(`${API_URL}/orders/${id}`, { status }),
};

export default api;
