import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardActions,
  Typography,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Paper,
  makeStyles,
} from '@material-ui/core';
import { useCart } from '../context/CartContext';
import api from '../api';

const useStyles = makeStyles((theme) => ({
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  filters: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(3),
  },
  filterForm: {
    display: 'flex',
    gap: theme.spacing(2),
  },
  formControl: {
    minWidth: 120,
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardContent: {
    flexGrow: 1,
  },
  price: {
    marginTop: theme.spacing(1),
  },
}));

function Products() {
  const classes = useStyles();
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [priceSort, setPriceSort] = useState('');
  const [stockFilter, setStockFilter] = useState('all');
  const { addToCart } = useCart();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await api.getProducts();
      setProducts(response.data);
    } catch (error) {
      console.error('Ошибка при получении товаров:', error);
    }
  };

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  const filteredProducts = products
    .filter((product) => {
      const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStock = stockFilter === 'all' || 
        (stockFilter === 'inStock' && product.stock > 0) ||
        (stockFilter === 'outOfStock' && product.stock === 0);
      return matchesSearch && matchesStock;
    })
    .sort((a, b) => {
      if (priceSort === 'asc') return a.price - b.price;
      if (priceSort === 'desc') return b.price - a.price;
      return 0;
    });

  return (
    <Container className={classes.container}>
      <Paper className={classes.filters}>
        <form className={classes.filterForm}>
          <TextField
            label="Поиск"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            variant="outlined"
            size="small"
          />
          <FormControl variant="outlined" className={classes.formControl} size="small">
            <InputLabel>Сортировка</InputLabel>
            <Select
              value={priceSort}
              onChange={(e) => setPriceSort(e.target.value)}
              label="Сортировка"
            >
              <MenuItem value="">По умолчанию</MenuItem>
              <MenuItem value="asc">Сначала дешевые</MenuItem>
              <MenuItem value="desc">Сначала дорогие</MenuItem>
            </Select>
          </FormControl>
          <FormControl variant="outlined" className={classes.formControl} size="small">
            <InputLabel>Наличие</InputLabel>
            <Select
              value={stockFilter}
              onChange={(e) => setStockFilter(e.target.value)}
              label="Наличие"
            >
              <MenuItem value="all">Все товары</MenuItem>
              <MenuItem value="inStock">В наличии</MenuItem>
              <MenuItem value="outOfStock">Нет в наличии</MenuItem>
            </Select>
          </FormControl>
        </form>
      </Paper>

      <Grid container spacing={4}>
        {filteredProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4}>
            <Card className={classes.card}>
              <CardContent className={classes.cardContent}>
                <Typography gutterBottom variant="h5" component="h2">
                  {product.name}
                </Typography>
                <Typography>{product.description}</Typography>
                <Typography variant="h6" className={classes.price}>
                  {product.price} ₽
                </Typography>
                <Typography color="textSecondary">
                  В наличии: {product.stock}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  size="small"
                  color="primary"
                  onClick={() => handleAddToCart(product)}
                  disabled={product.stock === 0}
                >
                  {product.stock === 0 ? 'Нет в наличии' : 'В корзину'}
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default Products;
